import React, { useState, useEffect } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import axios from 'axios';

const TokenSelection = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const [selectedMeal, setSelectedMeal] = useState('');
  const [error, setError] = useState('');
  const [confirming, setConfirming] = useState(false);

  const mealTypes = ['Beef', 'Chicken', 'Mutton', 'Duck'];

  // Get studentId from location state
  const studentId = location.state?.studentId;

  useEffect(() => {
    if (!studentId) {
      // If no studentId, redirect to sign in
      navigate('/');
    }
  }, [studentId, navigate]);

  const handleConfirm = async () => {
    if (!selectedMeal) {
      setError('Please select a meal type');
      return;
    }
    setError('');
    setConfirming(true);
    try {
      const response = await axios.post('http://localhost:5000/api/submit-token', {
        studentId,
        mealType: selectedMeal,
      });
      alert('Token confirmed!');
      navigate('/student-portal');
    } catch (err) {
      if (err.response && err.response.data && err.response.data.message) {
        setError(err.response.data.message);
      } else {
        setError('Server error. Please try again later.');
      }
    } finally {
      setConfirming(false);
    }
  };

  return (
    <div className="min-h-screen flex flex-col items-center justify-center bg-gradient-to-r from-purple-200 via-pink-200 to-red-200 p-8 font-serif">
      <h1 className="text-4xl font-extrabold mb-10 text-gray-900 drop-shadow-lg tracking-wide">Choose Your Token</h1>
      <div className="grid grid-cols-2 gap-8 mb-10 w-full max-w-md">
        {mealTypes.map((meal) => (
          <button
            key={meal}
            onClick={() => setSelectedMeal(meal)}
            className={`p-8 rounded-xl border-4 text-center font-semibold transition-transform transform ${
              selectedMeal === meal
                ? 'bg-gradient-to-r from-purple-700 to-pink-600 border-pink-700 text-white shadow-2xl scale-105'
                : 'bg-white border-gray-300 text-gray-900 hover:bg-pink-100 hover:border-pink-400 hover:shadow-lg hover:scale-105'
            }`}
          >
            {meal}
          </button>
        ))}
      </div>
      {error && <p className="text-red-700 mb-8 font-semibold text-lg">{error}</p>}
      <button
        onClick={handleConfirm}
        disabled={confirming}
        className="bg-gradient-to-r from-purple-700 to-pink-600 text-white py-4 px-12 rounded-xl hover:from-pink-600 hover:to-purple-700 transition-shadow shadow-lg disabled:opacity-60 disabled:cursor-not-allowed text-xl font-semibold"
      >
        {confirming ? 'Confirming...' : 'Confirm'}
      </button>
    </div>
  );
};

export default TokenSelection;
